/*eslint-disable*/

import axios from "axios";
import React, { useState } from "react";
import { API } from "config/Api";
import { useNavigate } from "react-router-dom";
// import { toast } from "react-toastify";

const ChatBotSideBar = () => {
  // States Management
  const [chatItems, setChatItems] = useState([]);
  const token = localStorage.getItem("Token");
  const navigate = useNavigate();
  // Function Calling

  const handleNewChatClick = () => {
    // Add a new chat item to the list
    setChatItems((prevItems) => [...prevItems, "New Chat created"]);
  };


  const handleLogOut = () => {
    localStorage.removeItem("Token");
    navigate("/");
    window.location.reload();

    // toast.success("Logged out Successfully!");
  };
  const handlePageView=()=>{

  }

  useEffect(()=>{
    axios.get(API.BASE_URL+"topics/", {
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    }).then((res)=>{
      // getChatHistory(res.data.data)
      console.log(res.data.data.name)
      setName(res.data.data)
      const chatHistory = res.data.data;
      const questionArray = chatHistory.map((item) => item.questions);
      const answerArray = chatHistory.map((item) => item.answer);
      const answerTIme = chatHistory.map((item) => item.time);
      const answerID = chatHistory.map((item) => item.id);

      setEffectReloadState(false);
      // Update the questions and answers state
      setQuestions(questionArray);
      setAnswers(answerArray);
      setAnswerTime(answerTIme);
      setAnswerID(answerID);
    }).catch((error)=>{
      console.log(error)
    })

  },[EffectReloadState])
  return (
    <div className="sidebar">
      <div className="new-chat-bttn">
        <div className="bttn-chat">
          <a className="flex px-3 " href="#chat" onClick={handleNewChatClick}>
            <svg
              stroke="currentColor"
              fill="none"
              strokeWidth="2"
              viewBox="0 0 24 24"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="icon-sm shrink-0"
              height="1em"
              width="1em"
              xmlns="http://www.w3.org/2000/svg"
            >
              <line x1="12" y1="5" x2="12" y2="19"></line>
              <line x1="5" y1="12" x2="19" y2="12"></line>
            </svg>
            <span className="truncate">New Chat</span>
          </a>
        </div>
      </div>
      {chatItems.map((item, index) => (
        // <div className="new-chat-bttn" key={index}>
        //   <div className="bttn-chat">
        //   <svg
        //     xmlns="http://www.w3.org/2000/svg"
        //     width="1em"
        //     height="1em"
        //     fill="none"
        //     stroke="currentColor"
        //     strokeLinecap="round"
        //     strokeLinejoin="round"
        //     strokeWidth="2"
        //     className="icon-sm"
        //     viewBox="0 0 24 24"
        //   >
        //     <path d="M21 15a2 2 0 01-2 2H7l-4 4V5a2 2 0 012-2h14a2 2 0 012 2z"></path>
        //   </svg>
        //     <a className="flex px-3" href="#chat">
        //       <span className="truncate">{item}</span>
        //     </a>
        //   </div>
        // </div>
        <ul>
        <li className="search-list-titles">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="1em"
            height="1em"
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            className="icon-sm"
            viewBox="0 0 24 24"
          >
            <path d="M21 15a2 2 0 01-2 2H7l-4 4V5a2 2 0 012-2h14a2 2 0 012 2z"></path>
          </svg>
          <a className="flex px-3" href="#chat" onClick={handlePageView}>
            <span className="truncate">{item}</span>
          </a>
        </li>
        {/* <span className="truncate">{item}</span> */}
      </ul>
      ))}

      <button className="logout" onClick={handleLogOut}>
        Log Out
      </button>
    </div>
  );
};
export default ChatBotSideBar;
