import Dashboard from "layouts/dashboard";
import Tables from "layouts/tables";
import Billing from "layouts/billing";
import RTL from "layouts/rtl";
import Notifications from "layouts/notifications";
import Profile from "layouts/profile";
import SignIn from "layouts/authentication/sign-in";
import SignUp from "layouts/authentication/sign-up";

const token = localStorage.getItem("Admin-Token");

// Define a function to check if the user is authenticated
const isAuthenticated = () => {
  return !!token; // Return true if the token exists
};

// Higher-order component (HOC) for protected routes
const withAuth = (Component) => {
  return isAuthenticated() ? Component : SignIn;
};

const routes = [
  {
    type: "collapse",
    name: "Dashboard",
    key: "dashboard",
    icon: <Icon fontSize="small">dashboard</Icon>,
    route: "/dashboard",
    component: withAuth(Dashboard), // Use withAuth for protected routes
  },
  {
    name: "Sign In",
    icon: <Icon fontSize="small">login</Icon>,
    route: "/authentication/sign-in",
    component: SignIn,
  },
  {
    type: "collapse",
    name: "Notifications",
    key: "notifications",
    icon: <Icon fontSize="small">notifications</Icon>,
    route: "/notifications",
    component: withAuth(Notifications), // Use withAuth for protected routes
  },
  {
    type: "collapse",
    name: "Profile",
    key: "profile",
    icon: <Icon fontSize="small">person</Icon>,
    route: "/profile",
    component: withAuth(Profile), // Use withAuth for protected routes
  },
  {
    type: "collapse",
    name: "Add User",
    key: "sign-up",
    icon: <Icon fontSize="small">assignment</Icon>,
    route: "/sign-up",
    component: withAuth(SignUp), // Use withAuth for protected routes
  },
];

export default routes;
