/*eslint-disable*/

// react-routers components
import { Link } from "react-router-dom";

// prop-types is library for typechecking of props
import PropTypes from "prop-types";

// @mui material components
import Card from "@mui/material/Card";
import Divider from "@mui/material/Divider";
import Tooltip from "@mui/material/Tooltip";
import Icon from "@mui/material/Icon";

// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";

// Material Dashboard 2 React base styles
import colors from "assets/theme/base/colors";
import typography from "assets/theme/base/typography";
import { useEffect, useState } from "react";
import { Grid } from "@mui/material";
import MDInput from "components/MDInput";
import MDButton from "components/MDButton";
function ProfileInfoCard({ title, description, info, action , shadow }) {
  const [showEditForm, setShowEditForm] = useState(false)
  const [userFirstName , setUserFirstName] = useState(null)
  const [userLastName , setUserLastName] = useState(null)
  const [userEmail , setUserEmail] = useState(null)
  const labels = [];
  const values = [];
  const { socialMediaColors } = colors;
  const { size } = typography;
  // useEffect(()=>{
    setUserEmail(info.email)
    const fullNameArray = info.fullName.split(" ");

  // Extract the first name and last name
  setUserFirstName(fullNameArray[0])
  setUserLastName(fullNameArray.slice(1).join(" "))

  console.log("First Name:", firstName);
  console.log("Last Name:", lastName);
  // })
  // Convert this form `objectKey` of the object key in to this `object key`
  Object.keys(info).forEach((el) => {
    if (el.match(/[A-Z\s]+/)) {
      const uppercaseLetter = Array.from(el).find((i) => i.match(/[A-Z]+/));
      const newElement = el.replace(uppercaseLetter, ` ${uppercaseLetter.toLowerCase()}`);

      labels.push(newElement);
    } else {
      labels.push(el);
    }
  });

  // Push the object values into the values array
  Object.values(info).forEach((el) => values.push(el));

  // Render the card info items
  const renderItems = labels.map((label, key) => (
    <MDBox key={label} display="flex" py={1} pr={2}>
      <MDTypography variant="button" fontWeight="bold" textTransform="capitalize">
        {label}: &nbsp;
      </MDTypography>
      <MDTypography variant="button" fontWeight="regular" color="text">
        &nbsp;{values[key]}
      </MDTypography>
    </MDBox>
  ));

  const handleShowPOP=()=>{
    setShowEditForm(true);
  }
  const handleCloseFeed=()=>{
    setShowEditForm(false);
  }
  // Render the card social media icons
  // const renderSocial = social.map(({ link, icon, color }) => (
  //   <MDBox
  //     key={color}
  //     component="a"
  //     href={link}
  //     target="_blank"
  //     rel="noreferrer"
  //     fontSize={size.lg}
  //     color={socialMediaColors[color].main}
  //     pr={1}
  //     pl={0.5}
  //     lineHeight={1}
  //   >
  //     {icon}
  //   </MDBox>
  // ));

  return (
    <Card sx={{ height: "100%", boxShadow: !shadow && "none" }}>
      <MDBox display="flex" justifyContent="space-between" alignItems="center" pt={2} px={2}>
        <MDTypography variant="h6" fontWeight="medium" textTransform="capitalize">
          {title}
        </MDTypography>
        <MDTypography component={Link} to={action.route} variant="body2" color="secondary" onClick={handleShowPOP}>
        <Tooltip placement="top">
          <Icon>edit</Icon>
        </Tooltip>
        </MDTypography>
      </MDBox>
      <MDBox p={2}>
        <MDBox mb={2} lineHeight={1}>
          <MDTypography variant="button" color="text" fontWeight="light">
            {description}
          </MDTypography>
        </MDBox>
        <MDBox opacity={0.3}>
          <Divider />
        </MDBox>
        <MDBox>
          {renderItems}
          {/* <MDBox display="flex" py={1} pr={2}>
            <MDTypography variant="button" fontWeight="bold" textTransform="capitalize">
              social: &nbsp;
            </MDTypography>
            {renderSocial}
          </MDBox> */}
        </MDBox>
      </MDBox>
      {showEditForm?(
          <div className='modal'>
            <div className='modal-content'>
            <div className="close-div">
            <button className="close-button" icon="close" onClick={handleCloseFeed}>
              <Icon>close</Icon>
            </button>
            </div>
             <Grid item>
              <MDTypography variant="h6" fontWeight="medium" textTransform="capitalize" textAlign="center" >
                Edit Profile
              </MDTypography>
              <MDBox pt={4} pb={3} px={3}>
                <MDBox component="form" role="form">
                  <MDBox mb={2}>
                    <MDInput type="text" label="First Name" value={userFirstName} fullWidth  />
                  </MDBox>
                  <MDBox mb={2}>
                    <MDInput type="text" label="Last Name" value={userLastName} fullWidth  />
                  </MDBox>
                  <MDBox mb={2}>
                    <MDInput type="email" label="Email" value={userEmail} fullWidth  />
                  </MDBox>
                  <MDBox mt={4} mb={1}>
                    <MDButton variant="gradient" color="info" fullWidth  >
                      Update
                    </MDButton>
                  </MDBox>
                </MDBox>
              </MDBox>
            </Grid>
            </div> 
         </div>
      ):<></>}
    </Card>
  );
}

// Setting default props for the ProfileInfoCard
ProfileInfoCard.defaultProps = {
  shadow: true,
};

// Typechecking props for the ProfileInfoCard
ProfileInfoCard.propTypes = {
  title: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  info: PropTypes.objectOf(PropTypes.string).isRequired,
  // social: PropTypes.arrayOf(PropTypes.object).isRequired,
  action: PropTypes.shape({
    route: PropTypes.string.isRequired,
    tooltip: PropTypes.string.isRequired,
  }).isRequired,
  shadow: PropTypes.bool,
};

export default ProfileInfoCard;
