/*eslint-disable*/
import React, {useEffect , useState} from "react";
import ChatBotSideBar from "./Sidebar";
import PreViewPage from "./ChatPreViewFirst";
import { API } from "config/Api";
import axios from "axios";
const ChatBot = () => {
  const accessToken = localStorage.getItem("Token");
  const [TopicsState, setTopicsState] = useState([])
  useEffect(()=>{
    axios.get(API.BASE_URL+"topics/",{
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    }).then((resp)=>{
      setTopicsState(resp.data.data)
    }).catch((error)=>{
      console.log(error)
    })
  },[])
  const [newAddState , setnewAddState] = useState(false);
  const navigate = useNavigate();
  // Function Calling

  const handleNewChatClick = () => {
    axios.post(API.BASE_URL+"topics/",{name:"Chat"},{
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    }).then((response)=>{
      console.log(response);
      setnewAddState(true);
    }).catch((error)=>{
      console.log(error)
    })
  };

  const handleLogOut = () => {
    localStorage.removeItem("Token");
    navigate("/");
    window.location.reload();
  };

  return (
    <div className="main">
      <div className="chat">
        <div className="side-contetnt">
            <div className="sidebar">
      <div className="new-chat-bttn">
        <div className="bttn-chat">
          <a className="flex px-3 " href="#chat" onClick={handleNewChatClick}>
            <svg
              stroke="currentColor"
              fill="none"
              strokeWidth="2"
              viewBox="0 0 24 24"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="icon-sm shrink-0"
              height="1em"
              width="1em"
              xmlns="http://www.w3.org/2000/svg"
            >
              <line x1="12" y1="5" x2="12" y2="19"></line>
              <line x1="5" y1="12" x2="19" y2="12"></line>
            </svg>
            <span className="truncate">New Chat</span>
          </a>
        </div>
      </div>
       {TopicsState.map((index) => (  
        <>
        <div className="new-chat-bttn" >
          <div className="bttn-chat">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="1em"
            height="1em"
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            className="icon-sm"
            viewBox="0 0 24 24"
          >
            <path d="M21 15a2 2 0 01-2 2H7l-4 4V5a2 2 0 012-2h14a2 2 0 012 2z"></path>
          </svg>
            <a className="flex px-3" href="#chat">
              <span className="truncate">{index.name}</span>
            </a>
          </div>
        </div>
      </>
       ))}
      <button className="logout" onClick={handleLogOut}>
        Log Out
      </button>
    </div>
        </div>
        <div className="content">
          <PreViewPage />
        </div>
      </div>
    </div>
  );
};
export default ChatBot;
